import base64

import pytest

from bitcoinformats import psbt

VECTORS_INVALID = (
    (
        "",
        "Could not parse",
    ),
    (
        "",
        "length does not match embedded transaction",
    ),
    (
        "",
        "does not contain a transaction",
    ),
    (
        "",
        "Duplicate key",
    ),
    (
        "",
        "Key data not allowed",
    ),
)


@pytest.mark.parametrize("psbt_base64, error", VECTORS_INVALID)
def test_invalid(psbt_base64, error):
    data = base64.b64decode(psbt_base64)
    with pytest.raises(psbt.PsbtError, match=error):
        psbt.parse(data)
